import classNames from "classnames";
import React from "react";

export default function HeroBlock({
  title,
  subtitle,
  imageUrl,
  backgroundTint,
  reversed,
}: {
  title: string;
  subtitle: string;
  imageUrl: string;
  backgroundTint?: number;
  reversed?: boolean;
  bottom?: JSX.Element;
}) {
  return (
    <div
      className={classNames(["hero", "min-lg-screen"], {
        [`bg-base-${backgroundTint}`]: backgroundTint,
        "bg-base": !backgroundTint,
      })}
    >
      <div
        className={classNames(["hero-content", "flex-col"], {
          "lg:flex-row-reverse": reversed,
          "lg:flex-row": !reversed,
        })}
      >
        <img
          src={imageUrl}
          className="max-w-xs rounded-lg shadow-2xl mx-8 my-8"
        />
        <div className="px-8 py-8">
          <h1 className="text-5xl font-bold">{title}</h1>
          <p className="py-6">{subtitle}</p>
          {bottom || ""}
        </div>
      </div>
    </div>
  );
}
