import classNames from "classnames";
import React from "react";

export default function HeroBlock({
  title,
  subtitle,
  imageUrls,
  backgroundTint,
  reversed,
  bottom,
}: {
  title: string;
  subtitle: string;
  imageUrls: string[];
  backgroundTint?: string;
  reversed?: boolean;
  bottom?: JSX.Element;
}) {
  return (
    <div
      className={classNames(["hero", "min-lg-screen"], {
        [`${backgroundTint}`]: backgroundTint,
        "bg-base": !backgroundTint,
      })}
    >
      <div
        className={classNames(["hero-content", "flex-col"], {
          "lg:flex-row-reverse": reversed,
          "lg:flex-row": !reversed,
        })}
      >
        <div className="relative">
          {imageUrls.map((imageUrl) => (
            <img
              src={imageUrl}
              className="max-w-xs rounded-lg shadow-2xl mx-8 my-8 z-10"
            />
          ))}
        </div>

        <div className="px-8 py-8">
          <h1 className="text-5xl font-bold">{title}</h1>
          <p className="py-6">{subtitle}</p>
          <p className="py-6">{bottom}</p>
        </div>
      </div>
    </div>
  );
}
